import React from "react";
import styled from "styled-components";
import Responsive from "../common/Responsive";
import Button from "../common/Button";
import palette from "../../lib/styles/palette";
import SubInfo from "../common/SubInfo";
import Tags from "../common/Tags";
import { Link } from "react-router-dom";
const PostListBlock=styled(Responsive)`
  margin-top:3rem;
`
const WritePostButtonWrapper=styled.div`
  display:flex;
  justify-content:flex-end;
  margin-bottom:3rem;
`
const PostItemBlock=styled.div`
  padding-top:3rem;
  padding-bottom:3rem;
  &:first-child{
    padding-top:0;
  }
  &+&{
    border-top:1px solid ${palette.gray[2]}
  }
  h2{
    font-size:2rem;
    margin-bottom:0;
    margin-top:0;
    &:hover{
      color:${palette.gray[6]}
    }
  }
  p{
    margin-top:2rem;
  }
`;


const PostItem=({posts})=>{
  const {publishedDate, user, tags, title, body, _id}=posts;
  return(
    <PostItemBlock>
      <h2><Link to={`/@${user.username}/${_id}`}>{title}</Link></h2>
      <SubInfo username={user.username} publishedDate={new Date(publishedDate)}></SubInfo>
      <Tags tags={tags} />
      <p>{body}</p>
    </PostItemBlock>
  )
}

const PostList=({posts,loading, error, showWrittenButton})=>{
  if(error){
    return <PostListBlock>Error Occurred!!</PostListBlock>
  }
  return(
    <PostListBlock>
      <WritePostButtonWrapper>
        {showWrittenButton &&(
          <Button cyan to="/write">
            new Post
          </Button>
        )}
      </WritePostButtonWrapper>
      {!loading && posts && (
        <div>
          {posts.map(post=>(
            <PostItem posts={post} key={post._id}/>
          ))}
        </div>
      )}
    </PostListBlock>
  )
}
export default PostList;
