import React from "react";
import styled from "styled-components";
import palette from "../../lib/styles/palette";
import Responsive from "../common/Responsive";

const PostViewerBlock=styled(Responsive)`
  margin-top:4rem;
`
const PostHead=styled.div`
  border-bottom:1px solid ${palette.gray[2]};
  padding-bottom:3rem;
  margin-bottom:3rem;
  h1{
    font-size:3rem;
    line-height:1.5;
    margin:0;
  }
`;
const SubInfo=styled.div`
  margin-top:1rem;
  color:${palette.gray[6]};

  span+span:before{
    color:${palette.gray[5]};
    padding-left:0.25rem;
    padding-right:0.25rem;
    content:'\\B7';
  }
`;
const Tags=styled.div`
  margin-top:0.5rem;
  .tag{
    display:inline-block;
    color:${palette.cyan[7]};
    text-decoration:none;
    margin-right:0.5rem;
    &:hover{
      color:${palette.cyan[6]};
    }
  }
`;
const PostContent=styled.div`
  font-size:1.3125rem;
  color:${palette.gray[8]};
`
const PostViewer=({post,error,loading})=>{
  if(error){
    if(error.response && error.response.status==404){
      return <PostViewerBlock>No Existing Post</PostViewerBlock>
    }
    return <PostViewerBlock>Error occurred!</PostViewerBlock>
  }
  //로딩 중이거나 아직 포스트 데이터가 없을 때
  if(loading||!post){
    return null;
  }
  return(
    <PostViewerBlock>
      <PostHead>
        <h1>Title</h1>
        <SubInfo>
          <span><b>tester</b></span>
        </SubInfo>
        <Tags>
          <div className="tag">#tag1</div>
          <div className="tag">#tag2</div>
          <div className="tag">#tag3</div>
          <div className="tag">#tag4</div>
        </Tags>
      </PostHead>
      <PostContent dangerouslySetInnerHTML={{__html:'<p>HTML this is <b>content</b></p>'}}></PostContent>
    </PostViewerBlock>
  )
}

export default PostViewer;
