//       <p>about Post contents....</p>
//     </PostItemBlock>
//   )
// }

const PostList=()=>{
  return(
    <PostListBlock>
      <WritePostButtonWrapper>
        <Button cyan to="/write">
          new Post
        </Button>
      </WritePostButtonWrapper>
      <div>
        <PostItem/>
        <PostItem/>
        <PostItem/>
      </div>
    </PostListBlock>
  )
}
const PostItem=()=>{
  return(
    <PostItemBlock>
      <h2>Title</h2>
      <SubInfo username="username" publishedDate={new Date()}></SubInfo>
      <Tags tags={['tag1','tag2','tag3']} />
      <p>Some of Post contents...</p>
    </PostItemBlock>
  )
}
export default PostList;
